# notebooks/01_data_prep.py - starter cells for Jupyter\n\nimport subprocess\nimport json\nfrom pathlib import Path\nimport pandas as pd\n\n# 1) Run the downloader (this will also convert to CSV)\nprint('Running download (this may take a while)...')\n# Note: Comment out the next line if you want to run the downloader manually\n# subprocess.run(['python','src/check_nhanes_downloads.py','--out','data/raw','--report','reports','--convert'])\n\n# 2) Inspect reports\nreport_dir = Path('reports')\nif report_dir.exists():\n    for p in report_dir.glob('*_report.json'):\n        print('\\nReport:', p)\n        print(json.dumps(json.load(open(p)), indent=2)[:1000])\n\n# 3) After running merge, load processed CSV if exists\nproc = Path('data/processed/merged_clean.csv')\nif proc.exists():\n    df = pd.read_csv(proc)\n    print('Merged data shape:', df.shape)\n    print(df['sleep_disorder'].value_counts(dropna=False))\nelse:\n    print('No merged_clean.csv found. Run merge_and_engineer.py first')\n